import React, { Component } from "react";
import { Card , Button , Container , Row , Col } from "react-bootstrap";
import "./style.css";
import repos  from "../../utils/repos.json"

class ProjectCard extends Component {
  constructor() {
    super();
    this.state = { repos : [...repos] }
  }
  render() {


    return repos.map( (projects) =>
          <Col>
            <Card style={{ width: '18rem' }}>
              <div className="overflow">
                <Card.Img variant="top" src={projects.image} className="card-img-top" />
              </div>
              <Card.Body>
                <Card.Title>{projects.name}</Card.Title>
                <Card.Text style={{ textAlign: 'center' , marginTop: '15%' }}>
                  {projects.description}
                </Card.Text>
                <Card.Body>
                  <Card.Link href={projects.repo}>Repository</Card.Link>
                  <Card.Link href={projects.demo}>Deployment</Card.Link>
                </Card.Body>
              </Card.Body>
            </Card>
          </Col>    
    );
  }  
}  

export default ProjectCard;
